import logo from "./logo.svg";
import "./App.css";
import Koulu from "./Koulu";
import { useState } from "react";
import Nappain from "./Nappain";
let nappaimet = ["1", "2", "3", "4", "+", "-", "="];
function App() {
  const [teksti, setTeksti] = useState(""); //          [a,b]
  const nappainPainettu = (x) => {
    if (x == "=") {
      setTeksti(eval(teksti));
      return;
    }
    setTeksti(teksti + x);
  };
  return (
    <div>
      <p>{teksti}</p>

      {nappaimet.map((nappain, index) => (
        <Nappain
          key={index}
          nappainPainettu={nappainPainettu}
          nappain={nappain}
        />
      ))}
    </div>
  );
}

export default App;

