import logo from "./logo.svg";
// import "./App.css";
import Luokka from "./Luokka"; // imports the code from the file Luokka.js

// types of inputs that are needed for making the tentti-app
const App = () => {
  return (
    <div>
      <button
        onClick={() => {
          console.log("Painoit muuten nappulaa");
        }} // gives the console.log on clicking the button
      />
      <input
        type="text"
        onChange={(event) => {
          console.log("teksti kentassa lukee", event.target.value);
        }} //gives back in real time what is written into the field
      />
      <input
        type="checkbox"
        onChange={(event) => {
          console.log("checkboxin arvo on", event.target.checked);
        }}
      />
    </div>
  );
};

export default App;

